// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { Router, RouterState } from '@angular/router';
import { DivIconDB } from '../divicon-db'
import { DivIconService } from '../divicon.service'

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-diviconstable',
  templateUrl: './divicons-table.component.html',
  styleUrls: ['./divicons-table.component.css'],
})
export class DivIconsTableComponent implements OnInit {

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of DivIcon instances
  selection: SelectionModel<DivIconDB> = new (SelectionModel)
  initialSelection = new Array<DivIconDB>()

  // the data source for the table
  divicons: DivIconDB[] = []
  matTableDataSource: MatTableDataSource<DivIconDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.divicons
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (diviconDB: DivIconDB, property: string) => {
      switch (property) {
        case 'ID':
          return diviconDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return diviconDB.Name;

        case 'SVG':
          return diviconDB.SVG;

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (diviconDB: DivIconDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the diviconDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += diviconDB.Name.toLowerCase()
      mergedContent += diviconDB.SVG.toLowerCase()

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private diviconService: DivIconService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of divicon instances
    public dialogRef: MatDialogRef<DivIconsTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.diviconService.DivIconServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getDivIcons()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Edit', 'Delete', // insertion point for columns to display
        "Name",
        "SVG",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "SVG",
      ]
      this.selection = new SelectionModel<DivIconDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    this.getDivIcons()
    this.matTableDataSource = new MatTableDataSource(this.divicons)
  }

  getDivIcons(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.divicons = this.frontRepo.DivIcons_array;

        // insertion point for variables Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let divicon of this.divicons) {
            let ID = this.dialogData.ID
            let revPointer = divicon[this.dialogData.ReversePointer as keyof DivIconDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(divicon)
            }
            this.selection = new SelectionModel<DivIconDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, DivIconDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          let sourceField = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as DivIconDB[]
          for (let associationInstance of sourceField) {
            let divicon = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as DivIconDB
            this.initialSelection.push(divicon)
          }

          this.selection = new SelectionModel<DivIconDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.divicons
      }
    )
  }

  // newDivIcon initiate a new divicon
  // create a new DivIcon objet
  newDivIcon() {
  }

  deleteDivIcon(diviconID: number, divicon: DivIconDB) {
    // list of divicons is truncated of divicon before the delete
    this.divicons = this.divicons.filter(h => h !== divicon);

    this.diviconService.deleteDivIcon(diviconID).subscribe(
      divicon => {
        this.diviconService.DivIconServiceChanged.next("delete")
      }
    );
  }

  editDivIcon(diviconID: number, divicon: DivIconDB) {

  }

  // display divicon in router
  displayDivIconInRouter(diviconID: number) {
    this.router.navigate(["github_com_fullstack_lang_gongleaflet_go-" + "divicon-display", diviconID])
  }

  // set editor outlet
  setEditorRouterOutlet(diviconID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongleaflet_go_editor: ["github_com_fullstack_lang_gongleaflet_go-" + "divicon-detail", diviconID]
      }
    }]);
  }

  // set presentation outlet
  setPresentationRouterOutlet(diviconID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongleaflet_go_presentation: ["github_com_fullstack_lang_gongleaflet_go-" + "divicon-presentation", diviconID]
      }
    }]);
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.divicons.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.divicons.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<DivIconDB>()

      // reset all initial selection of divicon that belong to divicon
      for (let divicon of this.initialSelection) {
        let index = divicon[this.dialogData.ReversePointer as keyof DivIconDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(divicon)

      }

      // from selection, set divicon that belong to divicon
      for (let divicon of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = divicon[this.dialogData.ReversePointer as keyof DivIconDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(divicon)
      }


      // update all divicon (only update selection & initial selection)
      for (let divicon of toUpdate) {
        this.diviconService.updateDivIcon(divicon)
          .subscribe(divicon => {
            this.diviconService.DivIconServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, DivIconDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedDivIcon = new Set<number>()
      for (let divicon of this.initialSelection) {
        if (this.selection.selected.includes(divicon)) {
          // console.log("divicon " + divicon.Name + " is still selected")
        } else {
          console.log("divicon " + divicon.Name + " has been unselected")
          unselectedDivIcon.add(divicon.ID)
          console.log("is unselected " + unselectedDivIcon.has(divicon.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let divicon = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as DivIconDB
      if (unselectedDivIcon.has(divicon.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<DivIconDB>) = new Array<DivIconDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          divicon => {
            if (!this.initialSelection.includes(divicon)) {
              // console.log("divicon " + divicon.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + divicon.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = divicon.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = divicon.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("divicon " + divicon.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<DivIconDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
