// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { Router, RouterState } from '@angular/router';
import { LayerGroupDB } from '../layergroup-db'
import { LayerGroupService } from '../layergroup.service'

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-layergroupstable',
  templateUrl: './layergroups-table.component.html',
  styleUrls: ['./layergroups-table.component.css'],
})
export class LayerGroupsTableComponent implements OnInit {

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of LayerGroup instances
  selection: SelectionModel<LayerGroupDB> = new (SelectionModel)
  initialSelection = new Array<LayerGroupDB>()

  // the data source for the table
  layergroups: LayerGroupDB[] = []
  matTableDataSource: MatTableDataSource<LayerGroupDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.layergroups
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (layergroupDB: LayerGroupDB, property: string) => {
      switch (property) {
        case 'ID':
          return layergroupDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return layergroupDB.Name;

        case 'DisplayName':
          return layergroupDB.DisplayName;

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (layergroupDB: LayerGroupDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the layergroupDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += layergroupDB.Name.toLowerCase()
      mergedContent += layergroupDB.DisplayName.toLowerCase()

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private layergroupService: LayerGroupService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of layergroup instances
    public dialogRef: MatDialogRef<LayerGroupsTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.layergroupService.LayerGroupServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getLayerGroups()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Edit', 'Delete', // insertion point for columns to display
        "Name",
        "DisplayName",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "DisplayName",
      ]
      this.selection = new SelectionModel<LayerGroupDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    this.getLayerGroups()
    this.matTableDataSource = new MatTableDataSource(this.layergroups)
  }

  getLayerGroups(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.layergroups = this.frontRepo.LayerGroups_array;

        // insertion point for variables Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let layergroup of this.layergroups) {
            let ID = this.dialogData.ID
            let revPointer = layergroup[this.dialogData.ReversePointer as keyof LayerGroupDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(layergroup)
            }
            this.selection = new SelectionModel<LayerGroupDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, LayerGroupDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          let sourceField = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as LayerGroupDB[]
          for (let associationInstance of sourceField) {
            let layergroup = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as LayerGroupDB
            this.initialSelection.push(layergroup)
          }

          this.selection = new SelectionModel<LayerGroupDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.layergroups
      }
    )
  }

  // newLayerGroup initiate a new layergroup
  // create a new LayerGroup objet
  newLayerGroup() {
  }

  deleteLayerGroup(layergroupID: number, layergroup: LayerGroupDB) {
    // list of layergroups is truncated of layergroup before the delete
    this.layergroups = this.layergroups.filter(h => h !== layergroup);

    this.layergroupService.deleteLayerGroup(layergroupID).subscribe(
      layergroup => {
        this.layergroupService.LayerGroupServiceChanged.next("delete")
      }
    );
  }

  editLayerGroup(layergroupID: number, layergroup: LayerGroupDB) {

  }

  // display layergroup in router
  displayLayerGroupInRouter(layergroupID: number) {
    this.router.navigate(["github_com_fullstack_lang_gongleaflet_go-" + "layergroup-display", layergroupID])
  }

  // set editor outlet
  setEditorRouterOutlet(layergroupID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongleaflet_go_editor: ["github_com_fullstack_lang_gongleaflet_go-" + "layergroup-detail", layergroupID]
      }
    }]);
  }

  // set presentation outlet
  setPresentationRouterOutlet(layergroupID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongleaflet_go_presentation: ["github_com_fullstack_lang_gongleaflet_go-" + "layergroup-presentation", layergroupID]
      }
    }]);
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.layergroups.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.layergroups.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<LayerGroupDB>()

      // reset all initial selection of layergroup that belong to layergroup
      for (let layergroup of this.initialSelection) {
        let index = layergroup[this.dialogData.ReversePointer as keyof LayerGroupDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(layergroup)

      }

      // from selection, set layergroup that belong to layergroup
      for (let layergroup of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = layergroup[this.dialogData.ReversePointer as keyof LayerGroupDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(layergroup)
      }


      // update all layergroup (only update selection & initial selection)
      for (let layergroup of toUpdate) {
        this.layergroupService.updateLayerGroup(layergroup)
          .subscribe(layergroup => {
            this.layergroupService.LayerGroupServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, LayerGroupDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedLayerGroup = new Set<number>()
      for (let layergroup of this.initialSelection) {
        if (this.selection.selected.includes(layergroup)) {
          // console.log("layergroup " + layergroup.Name + " is still selected")
        } else {
          console.log("layergroup " + layergroup.Name + " has been unselected")
          unselectedLayerGroup.add(layergroup.ID)
          console.log("is unselected " + unselectedLayerGroup.has(layergroup.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let layergroup = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as LayerGroupDB
      if (unselectedLayerGroup.has(layergroup.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<LayerGroupDB>) = new Array<LayerGroupDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          layergroup => {
            if (!this.initialSelection.includes(layergroup)) {
              // console.log("layergroup " + layergroup.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + layergroup.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = layergroup.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = layergroup.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("layergroup " + layergroup.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<LayerGroupDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
