  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (mapoptionsDB: MapOptionsDB, property: string) => {
      switch (property) {
        case 'ID':
          return mapoptionsDB.ID

        // insertion point for specific sorting accessor
        case 'Lat':
          return mapoptionsDB.Lat;

        case 'Lng':
          return mapoptionsDB.Lng;

        case 'Name':
          return mapoptionsDB.Name;

        case 'ZoomLevel':
          return mapoptionsDB.ZoomLevel;

        case 'UrlTemplate':
          return mapoptionsDB.UrlTemplate;

        case 'Attribution':
          return mapoptionsDB.Attribution;

        case 'MaxZoom':
          return mapoptionsDB.MaxZoom;

        case 'ZoomControl':
          return mapoptionsDB.ZoomControl?"true":"false";

        case 'AttributionControl':
          return mapoptionsDB.AttributionControl?"true":"false";

        case 'ZoomSnap':
          return mapoptionsDB.ZoomSnap;

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (mapoptionsDB: MapOptionsDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the mapoptionsDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += mapoptionsDB.Lat.toString()
      mergedContent += mapoptionsDB.Lng.toString()
      mergedContent += mapoptionsDB.Name.toLowerCase()
      mergedContent += mapoptionsDB.ZoomLevel.toString()
      mergedContent += mapoptionsDB.UrlTemplate.toLowerCase()
      mergedContent += mapoptionsDB.Attribution.toLowerCase()
      mergedContent += mapoptionsDB.MaxZoom.toString()
      mergedContent += mapoptionsDB.ZoomSnap.toString()

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private mapoptionsService: MapOptionsService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of mapoptions instances
    public dialogRef: MatDialogRef<MapOptionssTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.mapoptionsService.MapOptionsServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getMapOptionss()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Edit', 'Delete', // insertion point for columns to display
        "Lat",
        "Lng",
        "Name",
        "ZoomLevel",
        "UrlTemplate",
        "Attribution",
        "MaxZoom",
        "ZoomControl",
        "AttributionControl",
        "ZoomSnap",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Lat",
        "Lng",
        "Name",
        "ZoomLevel",
        "UrlTemplate",
        "Attribution",
        "MaxZoom",
        "ZoomControl",
        "AttributionControl",
        "ZoomSnap",
      ]
      this.selection = new SelectionModel<MapOptionsDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    this.getMapOptionss()
    this.matTableDataSource = new MatTableDataSource(this.mapoptionss)
  }

  getMapOptionss(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.mapoptionss = this.frontRepo.MapOptionss_array;

        // insertion point for variables Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let mapoptions of this.mapoptionss) {
            let ID = this.dialogData.ID
            let revPointer = mapoptions[this.dialogData.ReversePointer as keyof MapOptionsDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(mapoptions)
            }
            this.selection = new SelectionModel<MapOptionsDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, MapOptionsDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          let sourceField = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as MapOptionsDB[]
          for (let associationInstance of sourceField) {
            let mapoptions = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as MapOptionsDB
            this.initialSelection.push(mapoptions)
          }

          this.selection = new SelectionModel<MapOptionsDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.mapoptionss
      }
    )
  }

  // newMapOptions initiate a new mapoptions
