  newMapOptions() {
  }

  deleteMapOptions(mapoptionsID: number, mapoptions: MapOptionsDB) {
    // list of mapoptionss is truncated of mapoptions before the delete
    this.mapoptionss = this.mapoptionss.filter(h => h !== mapoptions);

    this.mapoptionsService.deleteMapOptions(mapoptionsID).subscribe(
      mapoptions => {
        this.mapoptionsService.MapOptionsServiceChanged.next("delete")
      }
    );
  }

  editMapOptions(mapoptionsID: number, mapoptions: MapOptionsDB) {

  }

  // display mapoptions in router
  displayMapOptionsInRouter(mapoptionsID: number) {
    this.router.navigate(["github_com_fullstack_lang_gongleaflet_go-" + "mapoptions-display", mapoptionsID])
  }

  // set editor outlet
  setEditorRouterOutlet(mapoptionsID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongleaflet_go_editor: ["github_com_fullstack_lang_gongleaflet_go-" + "mapoptions-detail", mapoptionsID]
      }
    }]);
  }

  // set presentation outlet
  setPresentationRouterOutlet(mapoptionsID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongleaflet_go_presentation: ["github_com_fullstack_lang_gongleaflet_go-" + "mapoptions-presentation", mapoptionsID]
      }
    }]);
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.mapoptionss.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.mapoptionss.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<MapOptionsDB>()

      // reset all initial selection of mapoptions that belong to mapoptions
      for (let mapoptions of this.initialSelection) {
        let index = mapoptions[this.dialogData.ReversePointer as keyof MapOptionsDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(mapoptions)

      }

      // from selection, set mapoptions that belong to mapoptions
      for (let mapoptions of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = mapoptions[this.dialogData.ReversePointer as keyof MapOptionsDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(mapoptions)
      }


      // update all mapoptions (only update selection & initial selection)
      for (let mapoptions of toUpdate) {
        this.mapoptionsService.updateMapOptions(mapoptions)
          .subscribe(mapoptions => {
            this.mapoptionsService.MapOptionsServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, MapOptionsDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedMapOptions = new Set<number>()
      for (let mapoptions of this.initialSelection) {
        if (this.selection.selected.includes(mapoptions)) {
          // console.log("mapoptions " + mapoptions.Name + " is still selected")
        } else {
          console.log("mapoptions " + mapoptions.Name + " has been unselected")
          unselectedMapOptions.add(mapoptions.ID)
          console.log("is unselected " + unselectedMapOptions.has(mapoptions.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let mapoptions = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as MapOptionsDB
      if (unselectedMapOptions.has(mapoptions.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<MapOptionsDB>) = new Array<MapOptionsDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          mapoptions => {
            if (!this.initialSelection.includes(mapoptions)) {
              // console.log("mapoptions " + mapoptions.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + mapoptions.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = mapoptions.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = mapoptions.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("mapoptions " + mapoptions.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<MapOptionsDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
