// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { Router, RouterState } from '@angular/router';
import { VLineDB } from '../vline-db'
import { VLineService } from '../vline.service'

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-vlinestable',
  templateUrl: './vlines-table.component.html',
  styleUrls: ['./vlines-table.component.css'],
})
export class VLinesTableComponent implements OnInit {

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of VLine instances
  selection: SelectionModel<VLineDB> = new (SelectionModel)
  initialSelection = new Array<VLineDB>()

  // the data source for the table
  vlines: VLineDB[] = []
  matTableDataSource: MatTableDataSource<VLineDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.vlines
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (vlineDB: VLineDB, property: string) => {
      switch (property) {
        case 'ID':
          return vlineDB.ID

        // insertion point for specific sorting accessor
        case 'StartLat':
          return vlineDB.StartLat;

        case 'StartLng':
          return vlineDB.StartLng;

        case 'EndLat':
          return vlineDB.EndLat;

        case 'EndLng':
          return vlineDB.EndLng;

        case 'Name':
          return vlineDB.Name;

        case 'ColorEnum':
          return vlineDB.ColorEnum;

        case 'DashStyleEnum':
          return vlineDB.DashStyleEnum;

        case 'LayerGroup':
          return (vlineDB.LayerGroup ? vlineDB.LayerGroup.Name : '');

        case 'IsTransmitting':
          return vlineDB.IsTransmitting;

        case 'Message':
          return vlineDB.Message;

        case 'IsTransmittingBackward':
          return vlineDB.IsTransmittingBackward;

        case 'MessageBackward':
          return vlineDB.MessageBackward;

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (vlineDB: VLineDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the vlineDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += vlineDB.StartLat.toString()
      mergedContent += vlineDB.StartLng.toString()
      mergedContent += vlineDB.EndLat.toString()
      mergedContent += vlineDB.EndLng.toString()
      mergedContent += vlineDB.Name.toLowerCase()
      mergedContent += vlineDB.ColorEnum.toLowerCase()
      mergedContent += vlineDB.DashStyleEnum.toLowerCase()
      if (vlineDB.LayerGroup) {
        mergedContent += vlineDB.LayerGroup.Name.toLowerCase()
      }
      mergedContent += vlineDB.IsTransmitting.toLowerCase()
      mergedContent += vlineDB.Message.toLowerCase()
      mergedContent += vlineDB.IsTransmittingBackward.toLowerCase()
      mergedContent += vlineDB.MessageBackward.toLowerCase()

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private vlineService: VLineService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of vline instances
    public dialogRef: MatDialogRef<VLinesTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.vlineService.VLineServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getVLines()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Edit', 'Delete', // insertion point for columns to display
        "StartLat",
        "StartLng",
        "EndLat",
        "EndLng",
        "Name",
        "ColorEnum",
        "DashStyleEnum",
        "LayerGroup",
        "IsTransmitting",
        "Message",
        "IsTransmittingBackward",
        "MessageBackward",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "StartLat",
        "StartLng",
        "EndLat",
        "EndLng",
        "Name",
        "ColorEnum",
        "DashStyleEnum",
        "LayerGroup",
        "IsTransmitting",
        "Message",
        "IsTransmittingBackward",
        "MessageBackward",
      ]
      this.selection = new SelectionModel<VLineDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    this.getVLines()
    this.matTableDataSource = new MatTableDataSource(this.vlines)
  }

  getVLines(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.vlines = this.frontRepo.VLines_array;

        // insertion point for variables Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let vline of this.vlines) {
            let ID = this.dialogData.ID
            let revPointer = vline[this.dialogData.ReversePointer as keyof VLineDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(vline)
            }
            this.selection = new SelectionModel<VLineDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, VLineDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          let sourceField = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as VLineDB[]
          for (let associationInstance of sourceField) {
            let vline = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as VLineDB
            this.initialSelection.push(vline)
          }

          this.selection = new SelectionModel<VLineDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.vlines
      }
    )
  }

  // newVLine initiate a new vline
  // create a new VLine objet
  newVLine() {
  }

  deleteVLine(vlineID: number, vline: VLineDB) {
    // list of vlines is truncated of vline before the delete
    this.vlines = this.vlines.filter(h => h !== vline);

    this.vlineService.deleteVLine(vlineID).subscribe(
      vline => {
        this.vlineService.VLineServiceChanged.next("delete")
      }
    );
  }

  editVLine(vlineID: number, vline: VLineDB) {

  }

  // display vline in router
  displayVLineInRouter(vlineID: number) {
    this.router.navigate(["github_com_fullstack_lang_gongleaflet_go-" + "vline-display", vlineID])
  }

  // set editor outlet
  setEditorRouterOutlet(vlineID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongleaflet_go_editor: ["github_com_fullstack_lang_gongleaflet_go-" + "vline-detail", vlineID]
      }
    }]);
  }

  // set presentation outlet
  setPresentationRouterOutlet(vlineID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongleaflet_go_presentation: ["github_com_fullstack_lang_gongleaflet_go-" + "vline-presentation", vlineID]
      }
    }]);
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.vlines.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.vlines.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<VLineDB>()

      // reset all initial selection of vline that belong to vline
      for (let vline of this.initialSelection) {
        let index = vline[this.dialogData.ReversePointer as keyof VLineDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(vline)

      }

      // from selection, set vline that belong to vline
      for (let vline of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = vline[this.dialogData.ReversePointer as keyof VLineDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(vline)
      }


      // update all vline (only update selection & initial selection)
      for (let vline of toUpdate) {
        this.vlineService.updateVLine(vline)
          .subscribe(vline => {
            this.vlineService.VLineServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, VLineDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedVLine = new Set<number>()
      for (let vline of this.initialSelection) {
        if (this.selection.selected.includes(vline)) {
          // console.log("vline " + vline.Name + " is still selected")
        } else {
          console.log("vline " + vline.Name + " has been unselected")
          unselectedVLine.add(vline.ID)
          console.log("is unselected " + unselectedVLine.has(vline.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let vline = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as VLineDB
      if (unselectedVLine.has(vline.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<VLineDB>) = new Array<VLineDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          vline => {
            if (!this.initialSelection.includes(vline)) {
              // console.log("vline " + vline.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + vline.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = vline.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = vline.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("vline " + vline.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<VLineDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
