// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { Router, RouterState } from '@angular/router';
import { XLRowDB } from '../xlrow-db'
import { XLRowService } from '../xlrow.service'

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-xlrowstable',
  templateUrl: './xlrows-table.component.html',
  styleUrls: ['./xlrows-table.component.css'],
})
export class XLRowsTableComponent implements OnInit {

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of XLRow instances
  selection: SelectionModel<XLRowDB> = new (SelectionModel)
  initialSelection = new Array<XLRowDB>()

  // the data source for the table
  xlrows: XLRowDB[] = []
  matTableDataSource: MatTableDataSource<XLRowDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.xlrows
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (xlrowDB: XLRowDB, property: string) => {
      switch (property) {
        case 'ID':
          return xlrowDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return xlrowDB.Name;

        case 'RowIndex':
          return xlrowDB.RowIndex;

        case 'XLSheet_Rows':
          return this.frontRepo.XLSheets.get(xlrowDB.XLSheet_RowsDBID.Int64)!.Name;

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (xlrowDB: XLRowDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the xlrowDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += xlrowDB.Name.toLowerCase()
      mergedContent += xlrowDB.RowIndex.toString()
      if (xlrowDB.XLSheet_RowsDBID.Int64 != 0) {
        mergedContent += this.frontRepo.XLSheets.get(xlrowDB.XLSheet_RowsDBID.Int64)!.Name.toLowerCase()
      }


      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private xlrowService: XLRowService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of xlrow instances
    public dialogRef: MatDialogRef<XLRowsTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.xlrowService.XLRowServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getXLRows()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Edit', 'Delete', // insertion point for columns to display
        "Name",
        "RowIndex",
        "XLSheet_Rows",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "RowIndex",
        "XLSheet_Rows",
      ]
      this.selection = new SelectionModel<XLRowDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    this.getXLRows()
    this.matTableDataSource = new MatTableDataSource(this.xlrows)
  }

  getXLRows(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.xlrows = this.frontRepo.XLRows_array;

        // insertion point for variables Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let xlrow of this.xlrows) {
            let ID = this.dialogData.ID
            let revPointer = xlrow[this.dialogData.ReversePointer as keyof XLRowDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(xlrow)
            }
            this.selection = new SelectionModel<XLRowDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, XLRowDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          let sourceField = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as XLRowDB[]
          for (let associationInstance of sourceField) {
            let xlrow = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as XLRowDB
            this.initialSelection.push(xlrow)
          }

          this.selection = new SelectionModel<XLRowDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.xlrows
      }
    )
  }

  // newXLRow initiate a new xlrow
  // create a new XLRow objet
  newXLRow() {
  }

  deleteXLRow(xlrowID: number, xlrow: XLRowDB) {
    // list of xlrows is truncated of xlrow before the delete
    this.xlrows = this.xlrows.filter(h => h !== xlrow);

    this.xlrowService.deleteXLRow(xlrowID).subscribe(
      xlrow => {
        this.xlrowService.XLRowServiceChanged.next("delete")
      }
    );
  }

  editXLRow(xlrowID: number, xlrow: XLRowDB) {

  }

  // display xlrow in router
  displayXLRowInRouter(xlrowID: number) {
    this.router.navigate(["github_com_fullstack_lang_gongxlsx_go-" + "xlrow-display", xlrowID])
  }

  // set editor outlet
  setEditorRouterOutlet(xlrowID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongxlsx_go_editor: ["github_com_fullstack_lang_gongxlsx_go-" + "xlrow-detail", xlrowID]
      }
    }]);
  }

  // set presentation outlet
  setPresentationRouterOutlet(xlrowID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongxlsx_go_presentation: ["github_com_fullstack_lang_gongxlsx_go-" + "xlrow-presentation", xlrowID]
      }
    }]);
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.xlrows.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.xlrows.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<XLRowDB>()

      // reset all initial selection of xlrow that belong to xlrow
      for (let xlrow of this.initialSelection) {
        let index = xlrow[this.dialogData.ReversePointer as keyof XLRowDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(xlrow)

      }

      // from selection, set xlrow that belong to xlrow
      for (let xlrow of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = xlrow[this.dialogData.ReversePointer as keyof XLRowDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(xlrow)
      }


      // update all xlrow (only update selection & initial selection)
      for (let xlrow of toUpdate) {
        this.xlrowService.updateXLRow(xlrow)
          .subscribe(xlrow => {
            this.xlrowService.XLRowServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, XLRowDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedXLRow = new Set<number>()
      for (let xlrow of this.initialSelection) {
        if (this.selection.selected.includes(xlrow)) {
          // console.log("xlrow " + xlrow.Name + " is still selected")
        } else {
          console.log("xlrow " + xlrow.Name + " has been unselected")
          unselectedXLRow.add(xlrow.ID)
          console.log("is unselected " + unselectedXLRow.has(xlrow.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let xlrow = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as XLRowDB
      if (unselectedXLRow.has(xlrow.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<XLRowDB>) = new Array<XLRowDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          xlrow => {
            if (!this.initialSelection.includes(xlrow)) {
              // console.log("xlrow " + xlrow.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + xlrow.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = xlrow.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = xlrow.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("xlrow " + xlrow.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<XLRowDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
