// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { Router, RouterState } from '@angular/router';
import { XLSheetDB } from '../xlsheet-db'
import { XLSheetService } from '../xlsheet.service'

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-xlsheetstable',
  templateUrl: './xlsheets-table.component.html',
  styleUrls: ['./xlsheets-table.component.css'],
})
export class XLSheetsTableComponent implements OnInit {

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of XLSheet instances
  selection: SelectionModel<XLSheetDB> = new (SelectionModel)
  initialSelection = new Array<XLSheetDB>()

  // the data source for the table
  xlsheets: XLSheetDB[] = []
  matTableDataSource: MatTableDataSource<XLSheetDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.xlsheets
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (xlsheetDB: XLSheetDB, property: string) => {
      switch (property) {
        case 'ID':
          return xlsheetDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return xlsheetDB.Name;

        case 'MaxRow':
          return xlsheetDB.MaxRow;

        case 'MaxCol':
          return xlsheetDB.MaxCol;

        case 'NbRows':
          return xlsheetDB.NbRows;

        case 'XLFile_Sheets':
          return this.frontRepo.XLFiles.get(xlsheetDB.XLFile_SheetsDBID.Int64)!.Name;

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (xlsheetDB: XLSheetDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the xlsheetDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += xlsheetDB.Name.toLowerCase()
      mergedContent += xlsheetDB.MaxRow.toString()
      mergedContent += xlsheetDB.MaxCol.toString()
      mergedContent += xlsheetDB.NbRows.toString()
      if (xlsheetDB.XLFile_SheetsDBID.Int64 != 0) {
        mergedContent += this.frontRepo.XLFiles.get(xlsheetDB.XLFile_SheetsDBID.Int64)!.Name.toLowerCase()
      }


      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private xlsheetService: XLSheetService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of xlsheet instances
    public dialogRef: MatDialogRef<XLSheetsTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.xlsheetService.XLSheetServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getXLSheets()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Edit', 'Delete', // insertion point for columns to display
        "Name",
        "MaxRow",
        "MaxCol",
        "NbRows",
        "XLFile_Sheets",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "MaxRow",
        "MaxCol",
        "NbRows",
        "XLFile_Sheets",
      ]
      this.selection = new SelectionModel<XLSheetDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    this.getXLSheets()
    this.matTableDataSource = new MatTableDataSource(this.xlsheets)
  }

  getXLSheets(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.xlsheets = this.frontRepo.XLSheets_array;

        // insertion point for variables Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let xlsheet of this.xlsheets) {
            let ID = this.dialogData.ID
            let revPointer = xlsheet[this.dialogData.ReversePointer as keyof XLSheetDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(xlsheet)
            }
            this.selection = new SelectionModel<XLSheetDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, XLSheetDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          let sourceField = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as XLSheetDB[]
          for (let associationInstance of sourceField) {
            let xlsheet = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as XLSheetDB
            this.initialSelection.push(xlsheet)
          }

          this.selection = new SelectionModel<XLSheetDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.xlsheets
      }
    )
  }

  // newXLSheet initiate a new xlsheet
  // create a new XLSheet objet
  newXLSheet() {
  }

  deleteXLSheet(xlsheetID: number, xlsheet: XLSheetDB) {
    // list of xlsheets is truncated of xlsheet before the delete
    this.xlsheets = this.xlsheets.filter(h => h !== xlsheet);

    this.xlsheetService.deleteXLSheet(xlsheetID).subscribe(
      xlsheet => {
        this.xlsheetService.XLSheetServiceChanged.next("delete")
      }
    );
  }

  editXLSheet(xlsheetID: number, xlsheet: XLSheetDB) {

  }

  // display xlsheet in router
  displayXLSheetInRouter(xlsheetID: number) {
    this.router.navigate(["github_com_fullstack_lang_gongxlsx_go-" + "xlsheet-display", xlsheetID])
  }

  // set editor outlet
  setEditorRouterOutlet(xlsheetID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongxlsx_go_editor: ["github_com_fullstack_lang_gongxlsx_go-" + "xlsheet-detail", xlsheetID]
      }
    }]);
  }

  // set presentation outlet
  setPresentationRouterOutlet(xlsheetID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongxlsx_go_presentation: ["github_com_fullstack_lang_gongxlsx_go-" + "xlsheet-presentation", xlsheetID]
      }
    }]);
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.xlsheets.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.xlsheets.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<XLSheetDB>()

      // reset all initial selection of xlsheet that belong to xlsheet
      for (let xlsheet of this.initialSelection) {
        let index = xlsheet[this.dialogData.ReversePointer as keyof XLSheetDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(xlsheet)

      }

      // from selection, set xlsheet that belong to xlsheet
      for (let xlsheet of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = xlsheet[this.dialogData.ReversePointer as keyof XLSheetDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(xlsheet)
      }


      // update all xlsheet (only update selection & initial selection)
      for (let xlsheet of toUpdate) {
        this.xlsheetService.updateXLSheet(xlsheet)
          .subscribe(xlsheet => {
            this.xlsheetService.XLSheetServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, XLSheetDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedXLSheet = new Set<number>()
      for (let xlsheet of this.initialSelection) {
        if (this.selection.selected.includes(xlsheet)) {
          // console.log("xlsheet " + xlsheet.Name + " is still selected")
        } else {
          console.log("xlsheet " + xlsheet.Name + " has been unselected")
          unselectedXLSheet.add(xlsheet.ID)
          console.log("is unselected " + unselectedXLSheet.has(xlsheet.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let xlsheet = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as XLSheetDB
      if (unselectedXLSheet.has(xlsheet.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<XLSheetDB>) = new Array<XLSheetDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          xlsheet => {
            if (!this.initialSelection.includes(xlsheet)) {
              // console.log("xlsheet " + xlsheet.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + xlsheet.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = xlsheet.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = xlsheet.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("xlsheet " + xlsheet.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<XLSheetDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
